import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Menu, Dropdown, Button, Row, Col } from "antd";
import "./index.scss";

const HeaderPage = () => {
  const navigate = useNavigate();

  const menuItems = [
    {
      label: "Trang chủ",
      key: "main",
    },
    {
      label: "Tra cứu học phần",
      key: "termSearch",
    },
    {
      label: "Thời khóa biểu",
      key: "schedule",
    },
  ];
  const personalInforItems = [
    {
      label: "Đăng xuất",
      key: "logout",
    },
    {
      label: "Xem thông tin cá nhân",
      key: "personalInfor",
    },
  ];
  return (
    <Row justify="space-between" className="header-container">
      <Col span={12}>
        <Menu mode="horizontal" items={menuItems} className="header-menu" />
      </Col>
      <Col span={4}>
        <Dropdown menu={{ personalInforItems }} placement="bottomLeft">
          <Button>{"12345678|Nguyen Van A"}</Button>
        </Dropdown>
      </Col>
    </Row>
  );
};
export default HeaderPage;
