import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Menu, Dropdown, Button, Row, Col } from "antd";
import "./index.scss";
import MenuItem from "antd/es/menu/MenuItem";

const items = [
  {
    key: "logout",
    label: "Đăng xuất",
  },
  {
    key: "personalInfor",
    label: "Xem thông tin cá nhân",
  },
];

const HeaderPage = () => {
  const navigate = useNavigate();

  const menuItems = [
    {
      label: "Trang chủ",
      key: "main",
    },
    {
      label: "Tra cứu học phần",
      key: "termSearch",
    },
    {
      label: "Thời khóa biểu",
      key: "schedule",
    },
  ];
  return (
    <div className="header-container">
      <Menu mode="horizontal" items={menuItems} className="header-menu" />
      <Dropdown
        menu={
          <Menu>
            <MenuItem key="logout" label="Đăng xuất" />
            <MenuItem key="personalInfor" label="Xem thông tin cá nhân" />
          </Menu>
        }
        placement="bottomRight"
      >
        <Button>123456789|Nguyen Van A</Button>
      </Dropdown>
    </div>
  );
};
export default HeaderPage;
