import React from "react";
import { Outlet, useLocation } from "react-router-dom";
import "./style.scss";
import { Col, Layout, Row } from "antd";
import HeaderPage from "./HeaderPage";
import SiderPage from "./SiderPage";
import FooterPage from "./FooterPage";
import ContentPage from "./ContentPage";
const { Header, Footer, Sider, Content } = Layout;
const AdminLayout = () => {
  const location = useLocation();
  const pathname = location.pathname;

  // Extract the page title from the pathname (you might need custom logic here)
  const pathParts = pathname.split("/").filter(Boolean);

  return (
    <Layout style={{ width: "100%" }}>
      <Header
        style={{
          display: "flex",
          alignItems: "center",
          padding: "0",
          background: "rgba(238, 238, 238, 1)",
        }}
      >
        <HeaderPage />
      </Header>

      <div className="main-body">
        <Row>
          <Layout hasSider className="main-container">
            <Col md={6} className="slider_container">
              <Sider className="slider_main">
                <SiderPage />
              </Sider>
            </Col>
            <Col md={18} className="content_container">
              <Content>
                <Outlet />
              </Content>
            </Col>
          </Layout>{" "}
        </Row>
      </div>
      <Footer>Footer</Footer>
    </Layout>
  );
};

export default AdminLayout;
