import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Menu, Dropdown, Button, Row, Col } from "antd";
import "./index.scss";

const items = [
  {
    key: "logout",
    label: "Đăng xuất",
  },
  {
    key: "personalInfor",
    label: "Xem thông tin cá nhân",
  },
];

const HeaderPage = () => {
  const navigate = useNavigate();

  const menuItems = [
    {
      label: "Trang chủ",
      key: "main",
    },
    {
      label: "Tra cứu học phần",
      key: "termSearch",
    },
    {
      label: "Thời khóa biểu",
      key: "schedule",
    },
    {
      label: <div className="information">123456789|Nguyen Van A</div>,
      key: null,
      children: [
        {
          key: "logout",
          label: "Đăng xuất",
        },
        {
          key: "personalInformation",
          label: "Xem thông tin cá nhân",
        },
      ],
    },
  ];
  return (
    <div className="header-container">
      <Menu mode="horizontal" items={menuItems} className="header-menu" />
      <Dropdown
        menu={{
          items,
        }}
        placement="bottomRight"
      >
        <Button>bottomRight</Button>
      </Dropdown>
    </div>
  );
};
export default HeaderPage;
